import { Injectable } from '@angular/core';
import { Task } from './model/task';
import { User } from './model/user';
import { Project } from './model/project';

@Injectable({
  providedIn: 'root'
})
export class TaskService {
  private nextId = 1;
  users: User[] = [
   { id: this.nextId++, firstName: 'Denis', lastName: 'Dalipi', username: "Deda", email: "denis.dalipi@unifr.ch", password: "1234567890" },
   { id: this.nextId++, firstName: 'Guillaume', lastName: 'Venier', username: "Guve", email: "guillaume.venier@unifr.ch", password: "1234567890" }
 ];

  tasks: Task[]= [
    {id: this.nextId++, name:"Assignment 3", description:"Finish assignment 3", date: new Date("2023-04-20"), isDone: false, assignedUsers: this.users},
    {id: this.nextId++, name:"Grade", description:"Get Good grade", date: new Date("2023-04-20"), isDone: false}
  ]

  getTasks(): Task[] {
    return this.tasks;
  }

  addTask(name: string, description: string, date: Date, assignedUsers: User[], assignedProjects?: Project[]): void {
  const task: Task = { id: this.nextId++, name, description, date, isDone: false };
  if (assignedUsers) {
    task.assignedUsers = assignedUsers;
  }
  if (assignedProjects) {
    task.assignedProjects = assignedProjects;
  }
  this.tasks.push(task);
}

  deleteTask(task: Task): void {
      const index = this.tasks.findIndex(u => u.id === task.id);
      if (index >= 0) {
        this.tasks.splice(index, 1);
      }
  }

  getNextId(): number {
    return this.nextId;
  }
}
