import { Component, OnInit } from '@angular/core';
import { MatTableDataSource } from '@angular/material/table';
import { Team } from '../model/team';
import { User } from '../model/user';
import { Project } from '../model/project';
import { TeamService } from '../team.service';
import { UserService } from '../user.service';
import { ProjectService } from '../project.service';

@Component({
  selector: 'app-team',
  templateUrl: './team.component.html',
  styleUrls: ['./team.component.css']
})
export class TeamComponent implements OnInit {
  teams: MatTableDataSource<Team> = new MatTableDataSource<Team>();
  users: User[] = [];
  projects: Project[] = [];
  editedTeam: Team | null = null;

  constructor(private teamService: TeamService, private userService: UserService, private projectService: ProjectService) {}

  ngOnInit() {
    this.getTeams();
    this.getUsers();
    this.getProjects();
  }

  getTeams(): void {
    this.teams.data = this.teamService.getTeams();
  }

  getUsers(): void {
    this.users = this.userService.getUsers();
  }

  getProjects(): void {
    this.projects = this.projectService.getProjects();
  }

  editTeam(team: Team): void {
    this.editedTeam = { ...team };
  }

  addTeam(name: string, description: string, members: User[], projectList: Project[]): void {
    name = name.trim();
    description = description.trim();
    if (!name || !description) {
      return;
    }
    if (this.editedTeam) {
      this.teamService.updateTeam({ ...this.editedTeam, name, description, members, projectList });
      this.editedTeam = null;
    } else {
      this.teamService.addTeam(name, description, members, projectList);
    }
    this.getTeams();
  }

  deleteTeam(team: Team): void {
    this.teamService.deleteTeam(team);
    this.getTeams();
  }

  saveChanges(): void {
    if (this.editedTeam) {
      this.teamService.updateTeam(this.editedTeam);
      this.editedTeam = null;
    }
    this.getTeams();
  }

  cancelEditing(): void {
    this.editedTeam = null;
  }
}
<div>
  <h2>Add Team</h2>
  <form>
    <label>
      Name:
      <input #name type="text" name="name" required>
    </label>
    <label>
      Description:
      <input #description type="text" name="description" required>
    </label>
    <label>
        Assign members:
        <select #members >
            <option *ngFor="let user of users" [value]="user">{{ user.firstName }} {{ user.lastName }}</option>
        </select>
    </label>
    <label>
        Assign projects:
        <select #projectList >
            <option *ngFor="let project of projects" [value]="project">{{ project.name }}</option>
        </select>
    </label>
    <button (click)="addTeam(name.value, description.value, users, projects); name.value=''; description.value=''">Add</button>
<div>
  <h2>Team List:</h2>
  <table>
    <thead>
      <tr>
        <th>ID</th>
        <th>Name</th>
        <th>Description</th>
        <th>Members</th>
        <th>Assigned projects</th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let team of teams">
        <td>{{ team.id }}</td>
        <td>{{ team.name }}</td>
        <td>{{ team.description }}</td>
        <td *ngIf="team.members">
            <div class="members">
                <span *ngFor="let user of team.members">
                    {{ user.firstName }} {{ user.lastName }}
                </span>
            </div>
        </td>
        <td *ngIf="team.projectList">
            <div class="projectList">
                <span *ngFor="let project of team.projectList">
                    {{ project.name }}
                </span>
            </div>
        </td>
        <td><button (click)="deleteTeam(team)">Delete</button></td>
      </tr>
    </tbody>
  </table>
</div>
