import { Component } from '@angular/core';
import { User } from '../model/user';
import { UserService } from '../user.service';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css']
})
export class UserComponent {
  users: User[] = [];
  editedUser: User | null = null;

  constructor(private userService: UserService) {}

  ngOnInit() {
    this.getUsers();
  }

  getUsers(): void {
    this.users = this.userService.getUsers();
  }

  editUser(user: User): void {
     this.editedUser = { ...user };
   }

  addUser(firstName: string, lastName: string, username: string, email: string, password: string): void {
    firstName = firstName.trim();
    lastName = lastName.trim();
    username = username.trim();
    email = email.trim();
    password = password.trim();
    if (!firstName || !lastName || !username || !email ) {
      return;
    }
    if (this.editedUser) {
        this.userService.updateUser(this.editedUser.id, firstName, lastName, username, email, password);
        this.editedUser = null;
    } else {
        this.userService.addUser(firstName, lastName, username, email, password);
    }
    this.getUsers();
  }

  deleteUser(user: User): void {
    this.users = this.users.filter(u => u !== user);
    this.userService.deleteUser(user);
  }
}
