import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

}
<div class="homepage-section">
  <h1>Welcome to My App</h1>
  <p>We're glad you're here. My App is a platform designed to help you manage your projects, tasks, teams, and users with ease. Our user-friendly interface and powerful features make it easy for you to stay organized and productive.</p>
  <button>Get Started</button>
</div>
