import { Injectable } from '@angular/core';
import { Project } from './model/project';
import { Task } from './model/task';

@Injectable({
  providedIn: 'root'
})
export class ProjectService {
  private nextId = 1;

  projects: Project[] = [
    {id: this.nextId++, name: "Master Task Manager",description: "To do list"},
    {id: this.nextId++,name: "Project 2",description: "Wallet application"}
  ];

  getProjects(): Project[] {
    return this.projects;
  }

  addProject(name: string, description: string) {
    const randomInt = Math.floor(Math.random() * 50) + 1;
    const newProject: Project = {
      name,
      description,
      projectId: randomInt,
    };
    this.allProjects.unshift(newProject);
  }

  deleteProject(project: Project) {
    this.allProjects.splice(this.allProjects.indexOf(project), 1);
  }
}
