import { Component, OnInit } from '@angular/core';
import { Task } from "../model/task";
import { TaskService } from '../task.service';
import { UserService } from '../user.service';
import { User } from '../model/user';
import { Project } from '../model/project';
import { ProjectService } from '../project.service';
import { MatTableDataSource } from '@angular/material/table';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.css']
})
export class TaskComponent implements OnInit {
  tasks: Task[] = [];
  dataSource: MatTableDataSource<Task> = new MatTableDataSource<Task>([]);


  users: User[] = [];
  projects: Project[] = [];
  displayedColumns: string[] = ['id', 'name', 'description', 'date', 'done', 'users', 'projects', 'actions'];
  taskForm: FormGroup;

  constructor(private taskService: TaskService, private userService: UserService, private projectService: ProjectService, private formBuilder: FormBuilder ) {
    this.taskForm = this.formBuilder.group({
      id: [''],
      name: ['', Validators.required],
      description: ['', Validators.required],
      date: ['', Validators.required],
      assignedUsers: [''],
      assignedProjects: [''],
    });
  }


  ngOnInit() {
    this.getTasks();
    this.getUsers();
    this.getProjects();
  }

  getUsers(): void {
    this.users = this.userService.getUsers();
  }

  getProjects(): void {
    this.projects = this.projectService.getProjects();
  }

  getTasks(): void {
   this.tasks = this.taskService.getTasks();
   this.dataSource = new MatTableDataSource(this.tasks);
 }

 saveTask(): void {
   const task = this.taskForm.value;
   if (task.id) {
     this.taskService.updateTask(task);
   } else {
     this.addNewTask(
       task.name,
       task.description,
       task.date,
       task.assignedUsers,
       task.assignedProjects
     );
   }
   this.taskForm.reset();
 }

 addNewTask(name: string, description: string, date: Date, assignedUsers: User[], assignedProjects: Project[]): void {
   this.taskService.addTask(name, description, date, assignedUsers, assignedProjects);
   this.getTasks();
 }

  deleteTask(task: Task): void {
    this.taskService.deleteTask(task);
    this.getTasks();
  }

  editTask(task: Task): void {
    this.taskForm.patchValue({ ...task, id: task.id });
  }

  getDisplayUsers(task: Task): string {
  return task.assignedUsers
    ? task.assignedUsers
        .map(user => `${user.firstName} ${user.lastName}`)
        .join(', ')
    : '';
    }

    getDisplayProjects(task: Task): string {
      return task.assignedProjects
        ? task.assignedProjects
            .map(project => `${project.name}`)
            .join(', ')
        : '';
    }

    onTaskStatusChange(task: Task): void {
        this.taskService.updateTask(task);
    }

}
<div>
  <h2>Add/Edit Task</h2>
  <form [formGroup]="taskForm">
    <mat-form-field>
      <mat-label>Name</mat-label>
      <input matInput formControlName="name" placeholder="Name" required />
    </mat-form-field>
    <mat-form-field>
      <mat-label>Description</mat-label>
      <input matInput formControlName="description" placeholder="Description" required />
    </mat-form-field>
    <mat-form-field>
      <mat-label>Date</mat-label>
      <input
        matInput
        formControlName="date"
        [matDatepicker]="picker"
        placeholder="Date"
        required
      />
      <mat-datepicker-toggle matSuffix [for]="picker"></mat-datepicker-toggle>
      <mat-datepicker #picker></mat-datepicker>
    </mat-form-field>
    <mat-form-field>
      <mat-label>Assign users</mat-label>
      <mat-select formControlName="assignedUsers" multiple>
          <mat-option *ngFor="let user of users" [value]="user">{{ user.firstName }}</mat-option>
        </mat-select>
      </mat-form-field>
      <mat-form-field>
        <mat-label>Assign projects</mat-label>
        <mat-select formControlName="assignedProjects" multiple>
          <mat-option *ngFor="let project of projects" [value]="project">{{ project.name }}</mat-option>
        </mat-select>
      </mat-form-field>
      <button mat-raised-button color="primary" (click)="saveTask()" [disabled]="taskForm.invalid">Save</button>
    </form>
  </div>

  <div>
    <h2>Task List</h2>
    <mat-table [dataSource]="dataSource">
      <ng-container matColumnDef="id">
        <mat-header-cell *matHeaderCellDef>ID</mat-header-cell>
        <mat-cell *matCellDef="let task">{{ task.id }}</mat-cell>
      </ng-container>
      <ng-container matColumnDef="name">
        <mat-header-cell *matHeaderCellDef>Name</mat-header-cell>
        <mat-cell *matCellDef="let task">{{ task.name }}</mat-cell>
      </ng-container>
      <ng-container matColumnDef="description">
        <mat-header-cell *matHeaderCellDef>Description</mat-header-cell>
        <mat-cell *matCellDef="let task">{{ task.description }}</mat-cell>
      </ng-container>
      <ng-container matColumnDef="date">
        <mat-header-cell *matHeaderCellDef>Date</mat-header-cell>
        <mat-cell *matCellDef="let task">{{ task.date | date }}</mat-cell>
      </ng-container>
      <ng-container matColumnDef="done">
  <mat-header-cell *matHeaderCellDef>Done</mat-header-cell>
  <mat-cell *matCellDef="let task">
    <mat-checkbox [(ngModel)]="task.isDone" (change)="onTaskStatusChange(task)"></mat-checkbox>
  </mat-cell>
</ng-container>

      <ng-container matColumnDef="users">
  <mat-header-cell *matHeaderCellDef>Users</mat-header-cell>
  <mat-cell *matCellDef="let task">{{ getDisplayUsers(task) }}</mat-cell>
</ng-container>
<ng-container matColumnDef="projects">
<mat-header-cell *matHeaderCellDef>Projects</mat-header-cell>
<mat-cell *matCellDef="let task">{{ getDisplayProjects(task) }}</mat-cell>
</ng-container>
      <ng-container matColumnDef="actions">
        <mat-header-cell *matHeaderCellDef>Actions</mat-header-cell>
        <mat-cell *matCellDef="let task">
          <button mat-icon-button color="primary" (click)="editTask(task)">
            <mat-icon>edit</mat-icon>
          </button>
          <button mat-icon-button color="warn" (click)="deleteTask(task)">
            <mat-icon>delete</mat-icon>
          </button>
        </mat-cell>
      </ng-container>

      <mat-header-row *matHeaderRowDef="displayedColumns"></mat-header-row>
      <mat-row *matRowDef="let row; columns: displayedColumns"></mat-row>
    </mat-table>
  </div>
