import { Component, OnInit } from '@angular/core';
import { MatTableDataSource } from '@angular/material/table';
import { User } from '../model/user';
import { UserService } from '../user.service';

@Component({
  selector: 'app-user',
  templateUrl: './user.component.html',
  styleUrls: ['./user.component.css'],
})
export class UserComponent implements OnInit {
  users: MatTableDataSource<User> = new MatTableDataSource<User>();
  editedUser: User | null = null;
  displayedColumns: string[] = ['id', 'firstName', 'lastName', 'username', 'email', 'action'];

  constructor(private userService: UserService) {}

  async ngOnInit() {
    await this.getUsers();
  }

  async getUsers(): Promise<void> {
    this.users.data = await this.userService.getUsers();
  }

  editUser(user: User): void {
    this.editedUser = { ...user };
  }

  async addUser(firstName: string, lastName: string, username: string, email: string, password: string): Promise<void> {
    firstName = firstName.trim();
    lastName = lastName.trim();
    username = username.trim();
    email = email.trim();
    password = password.trim();
    if (!firstName || !lastName || !username || !email) {
      return;
    }
    if (this.editedUser) {
      await this.userService.updateUser({ ...this.editedUser, firstName, lastName, username, email, password });
      this.editedUser = null;
    } else {
      await this.userService.addUser(firstName, lastName, username, email, password);
    }
    await this.getUsers();
  }

  async deleteUser(user: User): Promise<void> {
    await this.userService.deleteUser(user);
    await this.getUsers();
  }

  async saveChanges(): Promise<void> {
    if (this.editedUser) {
      await this.userService.updateUser(this.editedUser);
      this.editedUser = null;
    }
    await this.getUsers();
  }

  cancelEditing(): void {
    this.editedUser = null;
  }
}
<div>
  <h1>User Management</h1>
  <mat-table [dataSource]="users">
      <ng-container matColumnDef="id">
        <mat-header-cell *matHeaderCellDef>ID</mat-header-cell>
        <mat-cell *matCellDef="let user">{{ user.id }}</mat-cell>
      </ng-container>
    <ng-container matColumnDef="firstName">
      <mat-header-cell *matHeaderCellDef>First Name</mat-header-cell>
      <mat-cell *matCellDef="let user">{{ user.firstName }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="lastName">
      <mat-header-cell *matHeaderCellDef>Last Name</mat-header-cell>
      <mat-cell *matCellDef="let user">{{ user.lastName }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="username">
      <mat-header-cell *matHeaderCellDef>Username</mat-header-cell>
      <mat-cell *matCellDef="let user">{{ user.username }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="email">
      <mat-header-cell *matHeaderCellDef>Email</mat-header-cell>
      <mat-cell *matCellDef="let user">{{ user.email }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="action">
      <mat-header-cell *matHeaderCellDef>Action</mat-header-cell>
      <mat-cell *matCellDef="let user">
        <button mat-raised-button color="primary" (click)="editUser(user)">Edit</button>
        <button mat-raised-button color="warn" (click)="deleteUser(user)">Delete</button>
      </mat-cell>
    </ng-container>
    <mat-header-row *matHeaderRowDef="displayedColumns"></mat-header-row>
    <mat-row *matRowDef="let row; columns: displayedColumns"></mat-row>
  </mat-table>

  <div *ngIf="editedUser">
    <h2>Edit User</h2>
    <mat-form-field>
