import { Injectable } from '@angular/core';
import { Team } from './model/team';
import { Task } from './model/task';
import { User } from './model/user';
import { Project } from './model/project';

@Injectable({
  providedIn: 'root'
})
export class TeamService {
    private nextId = 1;

    teams: Team[]= [
      {id: 50, name:"Team 50", description:"Team 50 description"},
      {id: 51, name:"Team 51", description:"Team 51 description"}
    ]

    getTeams(): Team[] {
      return this.teams;
    }

    addTeam(name: string, description: string, members?: User[], projectList?: Project[],
    moduleList?: string[], taskList?: Task[]): void {
        const team: Team = { id: this.nextId++, name, description };
        if (members) {
            team.members = members;
        }
        if (taskList) {
            team.taskList = taskList;
        }
        if (projectList) {
            team.projectList = projectList;
        }
        this.teams.push(team);
    }

    deleteTeam(team: Team): void {
        const index = this.teams.findIndex(u => u.id === team.id);
        if (index >= 0) {
          this.teams.splice(index, 1);
        }
    }

    updateTeam(updatedTeam: Team): void {
        const index = this.teams.findIndex(t => t.id === updatedTeam.id);
        if (index >= 0) {
          this.teams[index] = updatedTeam;
        }
    }

    getNextId(): number {
      return this.nextId;
    }
}
