import { Component } from '@angular/core';
import { Task } from "../model/task";
import { TaskService } from '../task.service';
import { UserService } from '../user.service';
import { User } from '../model/user';
import { Project } from '../model/project';
import { ProjectService } from '../project.service';
import { MatTableDataSource } from '@angular/material/table';
import { MatSort } from '@angular/material/sort';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.css']
})
export class TaskComponent {
  tasks: MatTableDataSource<Task> = new MatTableDataSource<Task>([]);
  editedTask?: Task;
  users: User[] = [];
  projects: Project[] = [];
  selectedUsers: User[] = [];

  displayedColumns: string[] = ['id', 'name', 'description', 'date', 'isDone', 'assignedUsers', 'assignedProjects', 'action'];
  constructor(private taskService: TaskService, private userService: UserService, private projectService: ProjectService) {}

  ngOnInit() {
    this.getTasks();
    this.getUsers();
    this.getProjects();
  }

  getTasks(): void {
    this.tasks.data = this.taskService.getTasks();
  }

  getUsers(): void {
    this.users = this.userService.getUsers();
  }

  getProjects(): void {
    this.projects = this.projectService.getProjects();
  }

  addTask(name: string, description: string, dateString: string, assignedUsers: User[], assignedProjects: Project[]): void {
    name = name.trim();
    description = description.trim();
    if (!name || !description || !dateString) {
      return;
    }
    const dateParts = dateString.split('/');
    const date = new Date(parseInt(dateParts[2]), parseInt(dateParts[1]) - 1, parseInt(dateParts[0]));
    this.taskService.addTask(name, description, date, assignedUsers, assignedProjects);
    this.getTasks();
  }

  deleteTask(task: Task): void {
    this.taskService.deleteTask(task);
    this.getTasks();
  }

  editTask(task: Task): void {
    this.editedTask = { ...task };
    this.selectedUsers = this.editedTask.assignedUsers || [];
  }

  cancelEditing(): void {
    this.editedTask = undefined;
    this.selectedUsers = [];
  }

  updateTask(task: Task, name: string, description: string, date: Date, assignedUsers?: User[], assignedProjects?: Project[]): void {
    if (!name || !description || !date) {
      return;
    }
    this.taskService.updateTask(task.id, name, description, date, assignedUsers, assignedProjects);
    this.editedTask = undefined;
    this.selectedUsers = [];
    this.getTasks();
  }

  applyFilter(event: Event): void {
    const filterValue = (event.target as HTMLInputElement).value;
    this.tasks.filter = filterValue.trim().toLowerCase();
  }
}
<div class="container">
  <h2>Add Task</h2>
  <mat-card>
    <mat-card-content>
      <form>
        <mat-form-field appearance="fill">
          <mat-label>Name</mat-label>
          <input #name matInput name="name" required>
        </mat-form-field>
        <mat-form-field appearance="fill">
          <mat-label>Description</mat-label>
          <input #description matInput name="description" required>
        </mat-form-field>
        <mat-form-field appearance="fill">
          <mat-label>Date</mat-label>
          <input #date matInput name="date" pattern="(0?[1-9]|[12][0-9]|3[01])/(0?[1-9]|1[012])/\d{4}" placeholder="dd/MM/yyyy" required>
        </mat-form-field>
        <mat-form-field appearance="fill">
          <mat-label>Assign users</mat-label>
          <mat-select #assignedUsers [(ngModel)]="selectedUsers" multiple>
            <mat-option *ngFor="let user of users" [value]="user">
              {{ user.firstName }} {{ user.lastName }}
            </mat-option>
          </mat-select>
        </mat-form-field>
        <mat-form-field appearance="fill">
          <mat-label>Assign projects</mat-label>
          <mat-select #assignedProjects [(ngModel)]="selectedProjects" multiple>
            <mat-option *ngFor="let project of projects" [value]="project">
              {{ project.name }}
            </mat-option>
          </mat-select>
        </mat-form-field>
      </form>
    </mat-card-content>
    <mat-card-actions>
      <button mat-raised-button color="primary" (click)="addTask(name.value, description.value, date.value, selectedUsers, selectedProjects); name.value=''; description.value=''; date.value=''; selectedUsers = []; selectedProjects = [];">Add</button>
    </mat-card-actions>
  </mat-card>
</div>
<div class="task-list">
  <h2>Task List:</h2>
  <table mat-table [dataSource]="tasks" matSort>
    <ng-container matColumnDef="id">
      <th mat-header-cell *matHeaderCellDef mat-sort-header> ID </th>
      <td mat-cell *matCellDef="let task"> {{task.id}} </td>
    </ng-container>
    <ng-container matColumnDef="name">
      <th mat-header-cell *matHeaderCellDef mat-sort-header> Name </th>
      <td mat-cell *matCellDef="let task"> {{task.name}} </td>
    </ng-container>
    <ng-container matColumnDef="description">
      <th mat-header-cell *matHeaderCellDef mat-sort-header> Description </th>
      <td mat-cell *matCellDef="let task"> {{task.description}} </td>
    </ng-container>
    <ng-container matColumnDef="date">
      <th mat-header-cell *matHeaderCellDef mat-sort-header> Date </th>
      <td mat-cell *matCellDef="let task"> {{task.date | date:'dd/MM/yyyy'}} </td>
    </ng-container>
    <ng-container matColumnDef="done">
      <th mat-header-cell *matHeaderCellDef> Done </th>
      <td mat-cell *matCellDef="let task">
        <mat-checkbox [(ngModel)]="task.isDone" disabled></mat-checkbox>
      </td>
    </ng-container>
    <ng-container matColumnDef="users">
      <th mat-header-cell *matHeaderCellDef> Users </th>
      <td mat-cell *matCellDef="let task">
        <div class="assignedUsers">
          <span *ngFor="let user of task.assignedUsers">{{ user.firstName }} {{ user.lastName }}</span>
        </div>
      </td>
    </ng-container>
    <ng-container matColumnDef="projects">
      <th mat-header-cell *matHeaderCellDef> Projects </th>
      <td mat-cell *matCellDef="let task">
        <div class="assignedProjects">
          <span *ngFor="let project of task.assignedProjects">{{ project.name }}</span>
        </div>
      </td>
    </ng-container>
    <ng-container matColumnDef="actions">
      <th mat-header-cell *matHeaderCellDef> Actions </th>
      <td mat-cell *matCellDef="let task">
        <button mat-icon-button color="primary" (click)="editTask(task)">
          <mat-icon>edit</mat-icon>
        </button>
        <button mat-icon-button color="warn" (click)="deleteTask(task)">
          <mat-icon>delete</mat-icon>
        </button>
      </td>
    </ng-container>
    <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
    <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
  </table>
  <mat-paginator [pageSizeOptions]="[5, 10, 25, 100]" showFirstLastButtons></mat-paginator>
</div>
