import { Component, OnInit } from '@angular/core';
import { MatTableDataSource } from '@angular/material/table';
import { Team } from '../model/team';
import { User } from '../model/user';
import { Project } from '../model/project';
import { TeamService } from '../team.service';
import { UserService } from '../user.service';
import { ProjectService } from '../project.service';

@Component({
  selector: 'app-team',
  templateUrl: './team.component.html',
  styleUrls: ['./team.component.css']
})
export class TeamComponent implements OnInit {
  teams: MatTableDataSource<Team> = new MatTableDataSource<Team>();
  users: User[] = [];
  projects: Project[] = [];
  editedTeam: Team | null = null;
  displayedColumns: string[] = ['name', 'description', 'action'];

  constructor(private teamService: TeamService, private userService: UserService, private projectService: ProjectService) {}

  async ngOnInit() {
    await this.getTeams();
    await this.getUsers();
    this.getProjects();
  }

  async getTeams(): Promise<void> {
    this.teams.data = await this.teamService.getTeams();
  }

  async getUsers(): Promise<void> {
    this.users = await this.userService.getUsers();
  }

  async getProjects(): Promise<void> {
    this.projects = await this.projectService.getProjects();
  }

  editTeam(team: Team): void {
    this.editedTeam = { ...team };
  }

  async addTeam(name: string, description: string, members: User[], projectList: Project[]): Promise<void> {
    name = name.trim();
    description = description.trim();
    if (!name || !description) {
      return;
    }
    if (this.editedTeam) {
      await this.teamService.updateTeam({ ...this.editedTeam, name, description, members, projectList });
      this.editedTeam = null;
    } else {
      await this.teamService.addTeam(name, description, members, projectList);
    }
    await this.getTeams();
  }

  async deleteTeam(team: Team): Promise<void> {
    await this.teamService.deleteTeam(team);
    await this.getTeams();
  }

  async saveChanges(): Promise<void> {
    if (this.editedTeam) {
      await this.teamService.updateTeam(this.editedTeam);
      this.editedTeam = null;
    }
    await this.getTeams();
  }

  cancelEditing(): void {
    this.editedTeam = null;
  }
}
<div>
  <h1>Team Management</h1>
  <mat-table [dataSource]="teams">
    <ng-container matColumnDef="name">
      <mat-header-cell *matHeaderCellDef>Name</mat-header-cell>
      <mat-cell *matCellDef="let team">{{ team.name }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="description">
      <mat-header-cell *matHeaderCellDef>Description</mat-header-cell>
      <mat-cell *matCellDef="let team">{{ team.description }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="action">
      <mat-header-cell *matHeaderCellDef>Action</mat-header-cell>
      <mat-cell *matCellDef="let team">
        <button mat-raised-button color="primary" (click)="editTeam(team)">Edit</button>
        <button mat-raised-button color="warn" (click)="deleteTeam(team)">Delete</button>
      </mat-cell>
    </ng-container>
    <mat-header-row *matHeaderRowDef="displayedColumns"></mat-header-row>
    <mat-row *matRowDef="let row; columns: displayedColumns"></mat-row>
</mat-table>

<div *ngIf="editedTeam">
<h2>Edit Team</h2>
<mat-form-field>
  <mat-label>Name</mat-label>
  <input matInput [(ngModel)]="editedTeam.name">
</mat-form-field>
<mat-form-field>
  <mat-label>Description</mat-label>
  <input matInput [(ngModel)]="editedTeam.description">
</mat-form-field>
<button mat-raised-button color="primary" (click)="saveChanges()">Save</button>
<button mat-raised-button color="accent" (click)="cancelEditing()">Cancel</button>
</div>
<div *ngIf="!editedTeam">
<h2>Add Team</h2>
<mat-form-field>
  <mat-label>Name</mat-label>
  <input matInput #name>
</mat-form-field>
<mat-form-field>
  <mat-label>Description</mat-label>
  <input matInput #description>
</mat-form-field>
<button mat-raised-button color="primary" (click)="addTeam(name.value, description.value, [], [])">Add</button>
</div>
</div>
