import { Component, OnInit } from '@angular/core';
import { Task } from "../model/task";
import { TaskService } from '../task.service';
import { UserService } from '../user.service';
import { User } from '../model/user';
import { Project } from '../model/project';
import { ProjectService } from '../project.service';
import { MatTableDataSource } from '@angular/material/table';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.css']
})
export class TaskComponent implements OnInit {
  tasks: Task[] = [];
  dataSource: MatTableDataSource<Task> = new MatTableDataSource<Task>(); // Add this line

  users: User[] = [];
  projects: Project[] = [];
  selectedTask: Task = {} as Task;
  displayedColumns: string[] = ['id', 'name', 'description', 'date', 'isDone', 'assignedUsers', 'assignedProjects', 'actions'];


  constructor(private taskService: TaskService, private userService: UserService, private projectService: ProjectService) {}

  ngOnInit() {
    this.getTasks();
    this.getUsers();
    this.getProjects();
  }

  getTasks(): void {
    this.tasks = this.taskService.getTasks();
  }

  getUsers(): void {
    this.users = this.userService.getUsers();
  }

  getProjects(): void {
    this.projects = this.projectService.getProjects();
  }

  addTask(name: string, description: string, date: Date, assignedUsers: User[], assignedProjects: Project[]): void {
    this.taskService.addTask(name, description, date, assignedUsers, assignedProjects);
    this.getTasks();
  }

  deleteTask(task: Task): void {
    this.tasks = this.tasks.filter(u => u !== task);
    this.taskService.deleteTask(task);
  }

  openEditDialog(task: Task): void {
    this.selectedTask = { ...task };
  }

  editTask(task: Task): void {
  this.selectedTask = { ...task };
}


  saveTask(task: Task): void {
  const index = this.tasks.findIndex(t => t.id === task.id);
  if (index >= 0) {
    this.tasks[index] = task;
    this.taskService.updateTask(task);
    }
}

updateTask(task: Task): void {
const index = this.tasks.findIndex(t => t.id === task.id);
if (index >= 0) {
this.tasks[index] = task;
}
}


}
<div>
  <h2>Add/Edit Task</h2>
  <mat-form-field>
    <mat-label>Name</mat-label>
    <input matInput #name [(ngModel)]="selectedTask.name" placeholder="Name" required>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Description</mat-label>
    <input matInput #description [(ngModel)]="selectedTask.description" placeholder="Description" required>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Date</mat-label>
    <input matInput #date [(ngModel)]="selectedTask.date" [matDatepicker]="picker" placeholder="Date" required>
    <mat-datepicker-toggle matSuffix [for]="picker"></mat-datepicker-toggle>
    <mat-datepicker #picker></mat-datepicker>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Assign users</mat-label>
    <mat-select #assignedUsers [(ngModel)]="selectedTask.assignedUsers">
      <mat-option *ngFor="let user of users" [value]="user">{{ user.firstName }} {{ user.lastName }}</mat-option>
    </mat-select>
  </mat-form-field>
  <mat-form-field>
    <mat-label>Assign projects</mat-label>
    <mat-select #assignedProjects [(ngModel)]="selectedTask.assignedProjects">
      <mat-option *ngFor="let project of projects" [value]="project">{{ project.name }}</mat-option>
    </mat-select>
  </mat-form-field>
  <button mat-raised-button color="primary" (click)="saveTask(selectedTask)">Save</button>
</div>
<div>
  <h2>Task List:</h2>
  <mat-table [dataSource]="tasks">
    <ng-container matColumnDef="id">
      <mat-header-cell *matHeaderCellDef>ID</mat-header-cell>
      <mat-cell *matCellDef="let task">{{ task.id }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="name">
      <mat-header-cell *matHeaderCellDef>Name</mat-header-cell>
      <mat-cell *matCellDef="let task">{{ task.name }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="description">
      <mat-header-cell *matHeaderCellDef>Description</mat-header-cell>
      <mat-cell *matCellDef="let task">{{ task.description }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="date">
      <mat-header-cell *matHeaderCellDef>Date</mat-header-cell>
      <mat-cell *matCellDef="let task">{{ task.date | date:'dd/MM/yyyy' }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="done">
      <mat-header-cell *matHeaderCellDef>Done</mat-header-cell>
      <mat-cell *matCellDef="let task">
        <mat-checkbox [(ngModel)]="task.isDone"></mat-checkbox>
      </mat-cell>
    </ng-container>
    <ng-container matColumnDef="users">
      <mat-header-cell *matHeaderCellDef>User</mat-header-cell>
      <mat-cell *matCellDef="let task">
        <div *ngIf="task.assignedUsers">
          <span *ngFor="let user of task.assignedUsers">{{ user.firstName }} {{ user.lastName }}</span>
        </div>
      </mat-cell>
    </ng-container>
    <ng-container matColumnDef="projects">
  <mat-header-cell *matHeaderCellDef>Project</mat-header-cell>
  <mat-cell *matCellDef="let task">
    <div *ngIf="task.assignedProjects">
      <span *ngFor="let project of task.assignedProjects">{{ project.name }}</span>
    </div>
  </mat-cell>
</ng-container>
<ng-container matColumnDef="actions">
  <mat-header-cell *matHeaderCellDef>Actions</mat-header-cell>
  <mat-cell *matCellDef="let task">
    <button mat-icon-button color="primary" (click)="editTask(task)">
      <mat-icon>edit</mat-icon>
    </button>
    <button mat-icon-button color="warn" (click)="deleteTask(task)">
      <mat-icon>delete</mat-icon>
    </button>
  </mat-cell>
</ng-container>
<mat-header-row *matHeaderRowDef="displayedColumns"></mat-header-row>
<mat-row *matRowDef="let row; columns: displayedColumns;"></mat-row>
</mat-table>
</div>
