import { Component } from '@angular/core';
import { MatTableDataSource } from '@angular/material/table';
import { Project } from '../model/project';
import { ProjectService } from '../project.service';

@Component({
  selector: 'app-project',
  templateUrl: './project.component.html',
  styleUrls: ['./project.component.css']
})
export class ProjectComponent {
  projects: MatTableDataSource<Project> = new MatTableDataSource([]);
  editedProject: Project | null = null;

  constructor(private projectService: ProjectService) {}

  ngOnInit() {
    this.getProjects();
  }

  getProjects(): void {
    this.projects.data = this.projectService.getProjects();
  }

  editProject(project: Project): void {
    this.editedProject = { ...project };
  }

  addProject(name: string, description: string): void {
    name = name.trim();
    description = description.trim();
    if (!name || !description) {
      return;
    }
    if (this.editedProject) {
      this.projectService.updateProject(this.editedProject.id, name, description);
      this.editedProject = null;
    } else {
      this.projectService.addProject(name, description);
    }
    this.getProjects();
  }

  deleteProject(project: Project): void {
    this.projects.data = this.projects.data.filter(u => u !== project);
    this.projectService.deleteProject(project);
  }

  saveChanges(): void {
    if (this.editedProject) {
      this.projectService.updateProject(this.editedProject.id, this.editedProject.name, this.editedProject.description);
      this.editedProject = null;
    }
  }

  cancelEditing(): void {
    this.editedProject = null;
  }
}
