import { Injectable } from '@angular/core';
import { Task } from './task';
import { User } from './user';
import { Project } from './project';

@Injectable({
  providedIn: 'root'
})
export class TaskService {

  constructor() {}

  allTasks:Task[]= [
    {name:"Assignment 3",description:"Finish assignment 3",id:1, assignedUsers: [], assignedProjects: []},
    {name:"Grade",description:"Get Good grade",id:2, assignedUsers: [], assignedProjects: []}
  ]

  get tasks(){
    return this.allTasks;
  }

  addTask(name: string, description: string, assignedUsers:User[],assignedProjects:Project[]) {
    const randomInt = Math.floor(Math.random() * 50) + 1;
    this.allTasks.unshift({
      name,
      description,
      id: randomInt,
      assignedUsers,
      assignedProjects
    });
  }

  deleteTask(task: Task) {
    this.allTasks.splice(this.allTasks.indexOf(task), 1);
  }
}
