import { Component } from '@angular/core';
import { Task } from "../model/task";
import { TaskService } from '../task.service';
import { UserService } from '../user.service';
import { User } from '../model/user';
import { Project } from '../model/project';
import { ProjectService } from '../project.service';
import { MatSort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';

@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.css']
})
export class TaskComponent {
  tasks: MatTableDataSource<Task> = new MatTableDataSource<Task>([]);
  editedTask?: Task;
  displayedColumns: string[] = ['id', 'name', 'description', 'date', 'isDone', 'assignedUsers', 'assignedProjects', 'action'];
  users: User[] = [];
  projects: Project[] = [];
  @ViewChild(MatSort) sort!: MatSort;

  constructor(private taskService: TaskService, private userService: UserService, private projectService: ProjectService) {}

  ngOnInit() {
    this.getTasks();
    this.getUsers();
    this.getProjects();
    this.tasks.sort = this.sort;
  }

  getTasks(): void {
    this.tasks.data = this.taskService.getTasks();
  }

  getUsers(): void {
    this.users = this.userService.getUsers();
  }

  getProjects(): void {
    this.projects = this.projectService.getProjects();
  }

  addTask(name: string, description: string, dateString: string, assignedUsers: User[], assignedProjects: Project[]): void {
    name = name.trim();
    description = description.trim();
    if (!name || !description || !dateString) {
      return;
    }
    const dateParts = dateString.split('/');
    const date = new Date(parseInt(dateParts[2]), parseInt(dateParts[1]) - 1, parseInt(dateParts[0]));
    this.taskService.addTask(name, description, date, assignedUsers, assignedProjects);
    this.getTasks();
  }

  deleteTask(task: Task): void {
    this.taskService.deleteTask(task);
    this.getTasks();
  }

  editTask(task: Task): void {
    this.editedTask = { ...task };
  }

  cancelEditing(): void {
    this.editedTask = undefined;
  }

  updateTask(task: Task, name: string, description: string, date: Date, assignedUsers: User[], assignedProjects: Project): void {
    const index = this.tasks.data.findIndex(t => t.id === task.id);
    if (index >= 0) {
      const updatedTask: Task = {
        ...task,
        name,
        description,
        date,
        assignedUsers,
        assignedProjects
      };
      this.taskService.updateTask(updatedTask);
      this.tasks.data[index] = updatedTask;
    }
    this.editedTask = undefined;
  }

  applyFilter(event: Event): void {
    const filterValue = (event.target as HTMLInputElement).value;
    this.tasks.filter = filterValue.trim().toLowerCase();
  }
}
<div class="container">
  <h2>Add Task</h2>
  <form>
    <mat-form-field>
      <mat-label>Name</mat-label>
      <input matInput #name type="text" name="name" required>
    </mat-form-field>
    <mat-form-field>
      <mat-label>Description</mat-label>
      <input matInput #description type="text" name="description" required>
    </mat-form-field>
    <mat-form-field>
      <mat-label>Date</mat-label>
      <input matInput #date type="text" name="date" pattern="(0?[1-9]|[12][0-9]|3[01])/(0?[1-9]|1[012])/\d{4}" placeholder="dd/MM/yyyy" required>
    </mat-form-field>
    <mat-form-field>
      <mat-label>Assign users</mat-label>
      <mat-select #assignedUsers [(ngModel)]="selectedUsers" multiple>
        <mat-option *ngFor="let user of users" [value]="user">{{ user.firstName }} {{ user.lastName }}</mat-option>
      </mat-select>
    </mat-form-field>
    <mat-form-field>
      <mat-label>Assign projects</mat-label>
      <mat-select #assignedProjects [(ngModel)]="selectedProjects" multiple>
        <mat-option *ngFor="let project of projects" [value]="project">{{ project.name }}</mat-option>
      </mat-select>
    </mat-form-field>
  </form>
  <button mat-raised-button color="primary" (click)="addTask(name.value, description.value, date.value, selectedUsers, selectedProjects); name.value=''; description.value=''; date.value=''">Add</button>
</div>
<div>
  <h2>Task List:</h2>
  <mat-table [dataSource]="tasks" matSort>
    <ng-container matColumnDef="id">
      <mat-header-cell *matHeaderCellDef mat-sort-header>ID</mat-header-cell>
      <mat-cell *matCellDef="let task">{{ task.id }}</mat-cell>
    </ng-container>

    <ng-container matColumnDef="name">
      <mat-header-cell *matHeaderCellDef mat-sort-header>Name</mat-header-cell>
      <mat-cell *matCellDef="let task">{{ task.name }}</mat-cell>
    </ng-container>

    <ng-container matColumnDef="description">
      <mat-header-cell *matHeaderCellDef mat-sort-header>Description</mat-header-cell>
      <mat-cell *matCellDef="let task">{{ task.description }}</mat-cell>
    </ng-container>

    <ng-container matColumnDef="date">
      <mat-header-cell *matHeaderCellDef mat-sort-header>Date</mat-header-cell>
      <mat-cell *matCellDef="let task">{{ task.date | date:'dd/MM/yyyy' }}</mat-cell>
    </ng-container>

    <ng-container matColumnDef="isDone">
      <mat-header-cell *matHeaderCellDef mat-sort-header>Done</mat-header-cell>
      <mat-cell *matCellDef="let task">
        <mat-checkbox [(ngModel)]="task.isDone"></mat-checkbox>
      </mat-cell>
    </ng-container>

    <ng-container matColumnDef="assignedUsers">
      <mat-header-cell *matHeaderCellDef mat-sort-header>User</mat-header-cell>
      <mat-cell *matCellDef="let task">
        <div class="assignedUsers">
          <span *ngFor="let user of task.assignedUsers">
            {{ user.firstName }} {{ user.lastName }}
          </span>
        </div>
      </mat-cell>
    </ng-container>

    <ng-container matColumnDef="assignedProjects">
      <mat-header-cell *matHeaderCellDef mat-sort-header>Project</mat-header-cell>
      <mat-cell *matCellDef="let task">
        <div class="assignedProjects">
          <span *ngFor="let project of task.assignedProjects">
            {{ project.name }}
          </span>
        </div>
      </mat-cell>
    </ng-container>

    <ng-container matColumnDef="action">
      <mat-header-cell *matHeaderCellDef>Actions</mat-header-cell>
      <mat-cell *matCellDef="let task">
        <button mat-icon-button color="primary" (click)="editTask(task)">
          <mat-icon>edit</mat-icon>
        </button>
        <button mat-icon-button color="warn" (click)="deleteTask(task)">
          <mat-icon>delete</mat-icon>
        </button>
      </mat-cell>
    </ng-container>

    <mat-header-row *matHeaderRowDef="displayedColumns"></mat-header-row>
    <mat-row *matRowDef="let row; columns: displayedColumns;"></mat-row>
  </mat-table>
</div>
