import { Injectable } from '@angular/core';
import { Team } from './model/team';
import { Task } from './model/task';
import { User } from './model/user';
import { Project } from './model/project';

import { AppDb } from './app-db';



@Injectable({
  providedIn: 'root'
})
export class TeamService {
  private db: AppDb;

  constructor() {
    this.db = new AppDb();
  }


    async getTeams(): Promise<Team[]> {
      return await this.teams.toArray();
    }

    async addTeam(name: string, description: string, members: User[], projectList: Project[]): Promise<void> {
      await this.teams.add({ name, description, members, projectList });
    }

    async deleteTeam(team: Team): Promise<void> {
      await this.teams.delete(team.id);
    }


    async updateTeam(team: Team): Promise<void> {
      await this.teams.put(team);
    }
}
