import { Component, ViewChild } from '@angular/core';
import { MatSort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';
import { Project } from '../model/project';
import { ProjectService } from '../project.service';

@Component({
  selector: 'app-project',
  templateUrl: './project.component.html',
  styleUrls: ['./project.component.css']
})
export class ProjectComponent {
  projects: MatTableDataSource<Project> = new MatTableDataSource<Project>([]);
  editedProject?: Project;
  displayedColumns: string[] = ['id', 'name', 'description', 'action'];
  @ViewChild(MatSort) sort!: MatSort;

  constructor(private projectService: ProjectService) {}

  ngOnInit() {
    this.getProjects();
    this.projects.sort = this.sort;
  }

  getProjects(): void {
    this.projects.data = this.projectService.getProjects();
  }

  addProject(name: string, description: string): void {
    name = name.trim();
    description = description.trim();
    if (!name || !description) {
      return;
    }
    this.projectService.addProject(name, description);
    this.getProjects();
  }

  deleteProject(project: Project): void {
    this.projectService.deleteProject(project);
    this.getProjects();
  }

  editProject(project: Project): void {
    this.editedProject = { ...project };
  }

  cancelEditing(): void {
    this.editedProject = undefined;
  }

  updateProject(project: Project, name: string, description: string): void {
    const index = this.projects.data.findIndex(p => p.id === project.id);
    if (index >= 0) {
      this.projects.data[index].name = name;
      this.projects.data[index].description = description;
    }
  }

  saveChanges(): void {
    if (this.editedProject) {
      this.projectService.updateProject(this.editedProject.id, this.editedProject.name, this.editedProject.description);
      this.editedProject = undefined;
      this.getProjects();
    }
  }

  applyFilter(event: Event): void {
    const filterValue = (event.target as HTMLInputElement).value;
    this.projects.filter = filterValue.trim().toLowerCase();
  }
}
<div>
  <h1>Project Management</h1>
  <mat-table [dataSource]="projects">
    <ng-container matColumnDef="id">
      <mat-header-cell *matHeaderCellDef>ID</mat-header-cell>
      <mat-cell *matCellDef="let project">{{ project.id }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="name">
      <mat-header-cell *matHeaderCellDef>Name</mat-header-cell>
      <mat-cell *matCellDef="let project">{{ project.name }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="description">
      <mat-header-cell *matHeaderCellDef>Description</mat-header-cell>
      <mat-cell *matCellDef="let project">{{ project.description }}</mat-cell>
    </ng-container>
    <ng-container matColumnDef="action">
      <mat-header-cell *matHeaderCellDef>Action</mat-header-cell>
      <mat-cell *matCellDef="let project">
        <button mat-raised-button color="primary" (click)="editProject(project)">Edit</button>
        <button mat-raised-button color="warn" (click)="deleteProject(project)">Delete</button>
      </mat-cell>
    </ng-container>
    <mat-header-row *matHeaderRowDef="['id', 'name', 'description', 'action']"></mat-header-row>
    <mat-row *matRowDef="let row; columns: ['id', 'name', 'description', 'action']"></mat-row>
  </mat-table>

  <div *ngIf="editedProject">
    <h2>Edit Project</h2>
    <mat-form-field>
      <mat-label>Name</mat-label>
      <input matInput [(ngModel)]="editedProject.name">
    </mat-form-field>
    <mat-form-field>
      <mat-label>Description</mat-label>
      <input matInput [(ngModel)]="editedProject.description">
    </mat-form-field>
    <button mat-raised-button color="primary" (click)="saveChanges()">Save</button>
    <button mat-raised-button color="accent" (click)="cancelEditing()">Cancel</button>
  </div>
  <div *ngIf="!editedProject">
    <h2>Add Project</h2>
    <mat-form-field>
      <mat-label>Name</mat-label>
      <input matInput #name>
    </mat-form-field>
    <mat-form-field>
      <mat-label>Description</mat-label>
      <input matInput #description>
    </mat-form-field>
    <button mat-raised-button color="primary" (click)="addProject(name.value, description.value)">Add</button>
  </div>
</div>
