import { Component } from '@angular/core';
import { Task } from "../model/task";
import { TaskService } from '../task.service';
import { UserService } from '../user.service';
import { User } from '../model/user';
import { Project } from '../model/project';
import { ProjectService } from '../project.service';
import { Team } from '../model/team';
import { TeamService } from '../team.service';

@Component({
  selector: 'app-team',
  templateUrl: './team.component.html',
  styleUrls: ['./team.component.css']
})
export class TeamComponent {
    teams: Team[] = [];
    tasks: Task[] = [];
    users: User[] = [];
    projects: Project[] = [];

    constructor(private teamService: TeamService, private taskService: TaskService, private userService: UserService, private projectService: ProjectService) {}

    ngOnInit() {
      this.getTeams();
      this.getTasks();
      this.getUsers();
      this.getProjects();
    }

    getTeams(): void {
      this.teams = this.teamService.getTeams();
    }

    getTasks(): void {
      this.tasks = this.taskService.getTasks();
    }

    getUsers(): void {
      this.users = this.userService.getUsers();
    }

    getProjects(): void {
      this.projects = this.projectService.getProjects();
    }

    addTeam(name: string, description: string, administrator: User): void {
      name = name.trim();
      description = description.trim();
      if (!name || !description) {
        return;
      }
      this.teamService.addTeam(name, description, administrator);
      this.getTeams();
    }

    deleteTeam(team: Team): void {
      this.teams = this.teams.filter(u => u !== team);
      this.teamService.deleteTeam(team);
    }
}
