import { Injectable } from '@angular/core';
import { User } from './model/user';

@Injectable({
  providedIn: 'root'
})
export class UserService {
  private nextId = 1;
   users: User[] = [
    { id: this.nextId++, firstName: 'Denis', lastName: 'Dalipi', username: "Deda", email: "denis.dalipi@unifr.ch", password: "1234567890" },
    { id: this.nextId++, firstName: 'Guillaume', lastName: 'Venier', username: "Guve", email: "guillaume.venier@unifr.ch", password: "1234567890" }
  ];

  getUsers(): User[] {
    return this.users;
  }

  addUser(firstName: string, lastName: string, username: string, email: string, password: string): void {
    const user: User = { id: this.nextId++, firstName, lastName, username, email, password};
    this.users.push(user);
  }

  deleteUser(user: User): void {
    const index = this.users.findIndex(u => u.id === user.id);
    if (index >= 0) {
      this.users.splice(index, 1);
    }
  }

  updateUser(updatedUser: User): void {
    const index = this.users.findIndex(u => u.id === updatedUser.id);
    if (index >= 0) {
      this.users[index] = updatedUser;
    }
  }

  setName(firstName: string, lastName: string): void {
    firstName = firstName;
    lastName = lastName;
  }

  getNextId(): number {
      return this.nextId;
  }
}
