import { Injectable } from '@angular/core';
import { User } from './model/user';
import { AppDb } from './app-db';

@Injectable({
  providedIn: 'root',
})
export class UserService {
  private db: AppDb;

  constructor() {
    this.db = new AppDb();
  }

  async getUsers(): Promise<User[]> {
    return await this.db.users.toArray();
  }

  addUser(firstName: string, lastName: string, username: string, email: string, password: string): void {
      const user: User = { id: this.nextId++, firstName, lastName, username, email, password };
      this.users.add(user);
  }

  async deleteUser(user: User): Promise<void> {
    await this.db.users.delete(user.id);
  }

  async updateUser(updatedUser: User): Promise<void> {
    await this.db.users.put(updatedUser);
  }
}
