import { Component, ViewChild } from '@angular/core';
import { MatSort } from '@angular/material/sort';
import { MatTableDataSource } from '@angular/material/table';
import { Project } from '../model/project';
import { ProjectService } from '../project.service';

@Component({
  selector: 'app-project',
  templateUrl: './project.component.html',
  styleUrls: ['./project.component.css']
})
export class ProjectComponent {
  projects: MatTableDataSource<Project> = new MatTableDataSource<Project>([]);
  editedProject?: Project;
  displayedColumns: string[] = ['id', 'name', 'description', 'action'];
  @ViewChild(MatSort) sort!: MatSort;

  constructor(private projectService: ProjectService) {}

  ngOnInit() {
    this.getProjects();
    this.projects.sort = this.sort;
  }

  getProjects(): void {
    this.projects.data = this.projectService.getProjects();
  }

  addProject(name: string, description: string): void {
    name = name.trim();
    description = description.trim();
    if (!name || !description) {
      return;
    }
    this.projectService.addProject(name, description);
    this.getProjects();
  }

  deleteProject(project: Project): void {
    this.projectService.deleteProject(project);
    this.getProjects();
  }

  editProject(project: Project): void {
    this.editedProject = { ...project };
  }

  cancelEditing(): void {
    this.editedProject = undefined;
  }

  updateProject(project: Project, name: string, description: string): void {
    const index = this.projects.data.findIndex(p => p.id === project.id);
    if (index >= 0) {
      this.projects.data[index].name = name;
      this.projects.data[index].description = description;
    }
  }

  saveChanges(): void {
    if (this.editedProject) {
      this.projectService.updateProject(this.editedProject.id, this.editedProject.name, this.editedProject.description);
      this.editedProject = undefined;
      this.getProjects();
    }
  }

  applyFilter(event: Event): void {
    const filterValue = (event.target as HTMLInputElement).value;
    this.projects.filter = filterValue.trim().toLowerCase();
  }
}
<div>
  <h2>Add Project</h2>
  <form>
    <label>
      Name:
      <input #name type="text" name="name" required>
    </label>
    <label>
      Description:
      <input #description type="text" name="description" required>
    </label>
    <label>
      Start Date:
      <input #startDate type="text" name="startDate" pattern="(0?[1-9]|[12][0-9]|3[01])/(0?[1-9]|1[012])/\d{4}" placeholder="dd/MM/yyyy" required>
    </label>
    <label>
      End Date:
      <input #endDate type="text" name="endDate" pattern="(0?[1-9]|[12][0-9]|3[01])/(0?[1-9]|1[012])/\d{4}" placeholder="dd/MM/yyyy" required>
    </label>
    <label>
      Assign users:
      <select #assignedUsers multiple>
        <option *ngFor="let user of users" [value]="user">{{ user.firstName }} {{ user.lastName }}</option>
      </select>
    </label>
  </form>
  <button (click)="addProject(name.value, description.value, startDate.value, endDate.value, assignedUsers.selectedOptions); name.value=''; description.value=''; startDate.value=''; endDate.value='';">Add</button>
</div>
<div>
  <h2>Project List:</h2>
  <table>
    <thead>
      <tr>
        <th>ID</th>
        <th>Name</th>
        <th>Description</th>
        <th>Start Date</th>
        <th>End Date</th>
        <th>User</th>
        <th></th>
      </tr>
    </thead>
    <tbody>
      <tr *ngFor="let project of projects.data">
        <td>{{ project.id }}</td>
        <td>{{ project.name }}</td>
        <td>{{ project.description }}</td>
        <td>{{ project.startDate | date:'dd/MM/yyyy' }}</td>
        <td>{{ project.endDate | date:'dd/MM/yyyy' }}</td>
        <td>
          <div class="assignedUsers">
            <span *ngFor="let user of project.assignedUsers">{{ user.firstName }} {{ user.lastName }}</span>
          </div>
        </td>
        <td><button (click)="deleteProject(project)">Delete</button></td>
      </tr>
    </tbody>
  </table>
</div>
