import { Component, OnInit } from '@angular/core';
import { Task } from "../model/task";
import { TaskService } from '../task.service';
import { UserService } from '../user.service';
import { User } from '../model/user';
import { Project } from '../model/project';
import { ProjectService } from '../project.service';
import { MatTableDataSource } from '@angular/material/table';
import { FormBuilder, Validators } from '@angular/forms';
import { FormGroup } from '@angular/forms';



@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.css']
})
export class TaskComponent implements OnInit {
  tasks: Task[] = [];
  dataSource: MatTableDataSource<Task>;

  users: User[] = [];
  projects: Project[] = [];
  selectedTask: Task = {} as Task;
  displayedColumns: string[] = ['id', 'name', 'description', 'date', 'isDone', 'assignedUsers', 'assignedProjects', 'actions'];
  taskForm: FormGroup;

  constructor(private taskService: TaskService, private userService: UserService, private projectService: ProjectService, private formBuilder: FormBuilder ) {
    this.taskForm = this.formBuilder.group({
      name: ['', Validators.required],
      description: ['', Validators.required],
      date: ['', Validators.required],
      assignedUsers: [''],
      assignedProjects: [''],
    });
    this.dataSource = new MatTableDataSource<Task>([]); // Add this line
  }


  ngOnInit() {
    this.getTasks();
    this.getUsers();
    this.getProjects();
  }


  getUsers(): void {
    this.users = this.userService.getUsers();
  }

  getProjects(): void {
    this.projects = this.projectService.getProjects();
  }

  addTask(name: string, description: string, date: Date, assignedUsers: User[], assignedProjects: Project[]): void {
  this.taskService.addTask(name, description, date, assignedUsers, assignedProjects);
  this.getTasks();
}


  deleteTask(task: Task): void {
    this.tasks = this.tasks.filter(u => u !== task);
    this.taskService.deleteTask(task);
  }

  openEditDialog(task: Task): void {
    this.selectedTask = { ...task };
  }

  editTask(task: Task): void {
  this.selectedTask = { ...task };
}
getTasks(): void {
   this.tasks = this.taskService.getTasks();
   this.dataSource = new MatTableDataSource(this.tasks); // Add this line
 }


saveTask(): void {
  const task = this.taskForm.value;
  const index = this.tasks.findIndex((t) => t.id === task.id);
  if (index >= 0) {
    this.tasks[index] = task;
    this.taskService.updateTask(task);
  } else {
    this.addTask(
      task.name,
      task.description,
      task.date,
      task.assignedUsers,
      task.assignedProjects
    );
  }
  this.taskForm.reset();
}


updateTask(task: Task): void {
const index = this.tasks.findIndex(t => t.id === task.id);
if (index >= 0) {
this.tasks[index] = task;
}
}


