import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { resetDatabase } from '../reset-database';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  constructor(private router: Router) {}

  onGetStarted(): void {
    this.router.navigate(['/users']);
  }

  resetDatabase() {
      resetDatabase();
  }
}
<div class="homepage-section">
  <h1>Welcome to Master Task Manager</h1>
  <p>Master Task Manager App is a platform designed to help you manage your projects, tasks, teams, and users with ease.</p>
  <button (click)="onGetStarted()">Get Started</button>
  <button (click)="resetDatabase()">Get Started</button>

</div>
