import { Component } from '@angular/core';
import { Task } from "../model/task";
import { TaskService } from '../task.service';
import { UserService } from '../user.service';
import { User } from '../model/user';
import { Project } from '../model/project';
import { ProjectService } from '../project.service';


@Component({
  selector: 'app-task',
  templateUrl: './task.component.html',
  styleUrls: ['./task.component.css']
})
export class TaskComponent {
  tasks: Task[] = [];
  users: User[] = [];
  projects: Project[] = [];

  constructor(private taskService: TaskService, private userService: UserService, private projectService: ProjectService) {}

  ngOnInit() {
    this.getTasks();
    this.getUsers();
    this.getProjects();
  }

  getTasks(): void {
    this.tasks = this.taskService.getTasks();
  }

  getUsers(): void {
    this.users = this.userService.getUsers();
  }

  getProjects(): void {
    this.projects = this.projectService.getProjects();
  }

  addTask(name: string, description: string, dateString: string, assignedUsers: User[], assignedProjects: Project[]): void {
    name = name.trim();
    description = description.trim();
    if (!name || !description || !dateString) {
      return;
    }
    const dateParts = dateString.split('/');
    const date = new Date(parseInt(dateParts[2]), parseInt(dateParts[1]) - 1, parseInt(dateParts[0]));
    this.taskService.addTask(name, description, date, assignedUsers, assignedProjects);
    this.getTasks();
  }

  deleteTask(task: Task): void {
    this.tasks = this.tasks.filter(u => u !== task);
    this.taskService.deleteTask(task);
  }
}
