import { Injectable } from '@angular/core';
import { Project } from './model/project';

@Injectable({
  providedIn: 'root'
})
export class ProjectService {
  private nextId = 1;

  projects: Project[] = [
    {id: this.nextId++, name: "Master Task Manager",description: "To do list"},
    {id: this.nextId++,name: "Master Bank Wallet",description: "Wallet application"}
  ];

  getProjects(): Project[] {
    return this.projects;
  }

  addProject(name: string, description: string) {
    const project: Project = { id: this.nextId++, name, description};
    this.projects.push(project);
  }

  deleteProject(project: Project): void {
    const index = this.projects.findIndex(u => u.id === project.id);
    if (index >= 0) {
      this.projects.splice(index, 1);
    }
  }

  updateProject(id: number, name: string, description: string): void {
    const projectIndex = this.projects.findIndex(p => p.id === id);
    if (projectIndex >= 0) {
      this.projects[projectIndex] = { ...this.projects[projectIndex], name, description };
    }
  }

  getNextId(): number {
    return this.nextId;
  }
}
