import { Component } from '@angular/core';
import { TaskService } from './task.service';
import { Task } from './model/task';
import { User } from './model/user';
import { Project } from './model/project';
import { UserService } from './user.service';
import { ProjectService } from './project.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title= "Master Task Manager"
  tasks: Task[];
  assignedUsers: User[];
  assignedProjects: Project[];

  constructor(private taskService: TaskService, private userService:UserService, private projectService: ProjectService) {
    this.tasks = taskService.tasks;
    this.assignedUsers = userService.users;
    this.assignedProjects = projectService.projects;
  }

  addUser(firstName: string, lastName: string, username: string, email: string, password: string) {
    this.userService.addUser(firstName, lastName, username, email, password);
    this.assignedUsers = this.userService.users;
  }

  removeUser(user: User) {
    this.userService.deleteUser(user);
    this.assignedUsers= this.userService.users;
  }

  addProject(name: string, description: string){
    this.projectService.addProject(name, description);
    this.assignedProjects = this.projectService.projects;
  }

  removeProject(project: Project) {
    this.projectService.deleteProject(project);
    this.assignedProjects = this.projectService.projects;
  }

  addTask(name: string, description: string, date: Date, assignedUsers?: User[]) {
    this.taskService.addTask(name, description, date);

    this.tasks = this.taskService.tasks;
  }

  removeTask(task: Task) {
    this.taskService.deleteTask(task);
    this.tasks = this.taskService.tasks;
  }


}
