import { Injectable } from '@angular/core';
import { Task } from './model/task';
import { User } from './model/user';
import { Project } from './model/project';

@Injectable({
  providedIn: 'root'
})
export class TaskService {
  private nextId = 1;

  tasks: Task[]= [
    {id: this.nextId++, name:"Assignment 3", description:"Finish assignment 3", date: new Date("2023-04-20"), isDone: false, assignedUsers: []},
    {id: this.nextId++, name:"Grade", description:"Get Good grade", date: new Date("2023-04-20"), isDone: false}
  ]

  getTasks(): Task[] {
    return this.tasks;
  }

  addTask(name: string, description: string, date: Date) {
      const task: Task = { id: this.nextId++, name, description, date, isDone: false};
      this.tasks.push(task);
    }

  deleteTask(task: Task): void {
      const index = this.tasks.findIndex(u => u.id === task.id);
      if (index >= 0) {
        this.tasks.splice(index, 1);
      }
  }

  getNextId(): number {
    return this.nextId;
  }
}
