import { Injectable } from '@angular/core';
import { Project } from './project';
import { Task } from './task';

@Injectable({
  providedIn: 'root'
})
export class ProjectService {

  constructor() {}

  private allProjects: Project[] = [
    {name: "Project 1",description: "Master Task Manager",projectId: 1},
    {name: "Project 2",description: "Wallet application",projectId: 2}
  ];

  get projects(){
    return this.allProjects;
  }

  addProject(name: string, description: string) {
    const randomInt = Math.floor(Math.random() * 50) + 1;
    const newProject: Project = {
      name,
      description,
      projectId: randomInt,
    };
    this.allProjects.unshift(newProject);
  }

  deleteProject(project: Project) {
    this.allProjects.splice(this.allProjects.indexOf(project), 1);
  }
}
