import { Injectable } from '@angular/core';
import { Task } from './model/task';
import { User } from './model/user';
import { Project } from './model/project';

@Injectable({
  providedIn: 'root'
})
export class TaskService {
  private nextId = 1;

  tasks: Task[]= [
    {id: this.nextId++, name:"Assignment 3",description:"Finish assignment 3"},
    {id: this.nextId++, name:"Grade",description:"Get Good grade"}
  ]

  getTasks(): Task[] {
    return this.tasks;
  }

  addTask(name: string, description: string, assignedUsers:User[],assignedProjects:Project[]) {
    const randomInt = Math.floor(Math.random() * 50) + 1;
    this.allTasks.unshift({
      name,
      description,
      id: randomInt,
      assignedUsers,
      assignedProjects
    });
  }

  deleteTask(task: Task) {
    this.allTasks.splice(this.allTasks.indexOf(task), 1);
  }
}
